targets.expr:
(
  sum(increase(core_tracker_success_duties_total{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    cluster_peer=~"$cluster_peer",
    duty=~"attester|proposer|sync_message"
  }[$__range])) 
  by (duty)
)
/
(
  sum(increase(core_tracker_expect_duties_total{
    cluster_name="$cluster_name",
    cluster_hash="$cluster_hash",
    cluster_peer=~"$cluster_peer",
    duty=~"attester|proposer|sync_message"
  }[$__range])) 
  by (duty)
) > 0
